insert into tree(id, name, content, asset_id)
values (1, '자작나무', '순백색으로 일부 지역에서는 신성시하였다. 탈 때 \'자작 자작\' 소리가 난다고 한다.', 'TFF_Birch_Tree_01A'),
(2, '떡갈나무', '잎에 방부제 성분이 있어 찹쌀떡을 잎에 싸서 쪄먹는다. 때문에 떡갈나무라 불린다.', 'TFF_Oak_Tree_01A'),
(3, '소나무', '천연 제초제 성분이 있어 함께 자랄 수 있는 식물이 거의 없다. 주변에는 잡초가 없다.', 'TFF_Pine_Tree_01A'),
(4, '조팝나무', '나무의 꽃 모양이 조로 지은 밥인 조밥처럼 생겨서 조팝나무라고 불린다. 놀리지 말자.', 'TFF_Oak_Tree_01C'),
(5, '은행나무', '가을이 되면 낙엽이 지기 전에 잎사귀가 샛노랗게 물들어 아름답고, 병해충에 강하다. 냄새가 강렬하니 발 밑을 조심하자.', 'TFF_Oak_Tree_01B'),
(6, '사과나무', '8~9월에 꽃받침이 자라면서 녹색 혹은 붉은색의 맛있는 사과가 열린다. 사과는 맛있다.', 'PT_Fruit_Tree_01_apples'),
(7, '배나무', '돌배나무를 일본에서 개량한 종으로 많은 물을 필요로 한다. 맛있는 배가 열리는 나무이다. 배는 맛있다.', 'PT_Fruit_Tree_01_pears'),
(8, '자두나무', '자두가 열리는 나무이다. 벚나무속으로 꽃도 예쁘다. 맛있고 예쁜 팔방미인 나무이다.', 'PT_Fruit_Tree_01_plums'),
(9, '가문비나무', '보통 사람들이 생각하는 크리스마스 트리의 모양으로 크리스마스에 많이 사용된다.', 'PT_Generic_Tree_05_green'),
(10, '버드나무', '아래로 축 늘어진 가지와 길쭉길쭉한 잎이 트레이드마크인 유명한 나무이다.', 'PT_Weeping_Willow_Tree_01_green'),
(11, '느티나무', '쌍떡잎식물강 쐐기풀목 느릅나무과의 낙엽활엽수이다. 빠르게 발음해보자. 쌍떵닢싱물..ㄱ', 'PT_Generic_Tree_01_green'),
(12, '둥근소나무', '관상용으로 둥글게 만든 소나무이다. 정원을 예쁘게 꾸미기 좋다.', 'PT_Generic_Shrub_01_green'),
(13, '에메랄드그린', '이름에 맞게 아름다운 에메랄드그린색을 띠고 있다. 정원을 꾸밀 때 많이 사용된다.', 'NatureTreeFir02_3'),
(14, '박달나무', '자작나뭇과지만, 자작나무와 달리 껍질색이 어둡다. 매우 단단해 목재 기구 재료로 쓰인다.', 'SM_Env_Tree_Meadow_01'),
(15, '단풍나무', '가을을 상징하는 나무이다. 손바닥 모양으로 갈라진 잎사귀가 특징으로 가을 탈 때 생각하자.', 'SM_Env_Tree_Fruit_01');

